    display: block;
    padding: 5px;
`

const ProductInfo = styled.div`
    background: #e5e5e5;
    padding: 5px;
`

const ProductTitle = styled.p`
    text-align: center;
    margin: 10px;
`

const ProductPrice = styled.p`
    margin-top: 10px;
    margin-left: 10px;
`
